SELECT
  A.NR_TABLE, 
  A.TABLE_DENUMIRE,
  A.RIND_DENUMIRE,
  TO_CHAR(ROWNUM) AS ORDINE,
  TO_CHAR(ROWNUM) AS RIND,
  A.COL1,
  A.COL2,
  A.COL3,
  A.COL4,
  A.COL5,
  A.COL6,
  A.COL7,
  A.COL8
FROM
(SELECT
  1 AS NR_TABLE, 'CAEM2' AS TABLE_DENUMIRE,
  'CAEM '|| A.CAEM2 AS RIND_DENUMIRE,
  CC.FULL_CODE AS ORDINE,
  A.CAEM2 AS RIND,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.1.1') AND NVAL(A.COL1) = 1 AND C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL1,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.1.1') AND NVAL(A.COL2) = 1 AND  C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL2,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.1.2') AND NVAL(A.COL1) = 1 AND C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL3,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.1.2') AND NVAL(A.COL2) = 1 AND  C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL4,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.2.1') AND NVAL(A.COL1) = 1 AND C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL5,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.2.1') AND NVAL(A.COL2) = 1 AND  C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL6,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.2.2') AND NVAL(A.COL1) = 1 AND C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL7,
  COUNT(DISTINCT CASE WHEN A.RIND IN ('1.2.2') AND NVAL(A.COL2) = 1 AND  C.FULL_CODE LIKE '%'||CC.CODUL||';%' THEN A.CUIIO END) AS COL8
  
 
FROM
(SELECT
  D.ANUL,
  D.CUIIO,
  D.CUATM,
  D.CAEM2,
  D.CAPITOL,
  D.RIND,
